function next_designer_cursor() {
  var isBricks = window.location.href.includes("brickspreview");
  document
    .querySelectorAll(".next-designer-cursor")
    .forEach((designerCursor) => {
      if (isBricks) {
        designerCursor.style.display = "none";
      } else {
        designerCursor.classList.remove("next-designer-cursor--to-hide");
        if (designerCursor.getAttribute("data-target") != "") {
          var target = document.querySelector(
            designerCursor.getAttribute("data-target")
          );
          if (target) {
            if (designerCursor.getAttribute("data-type") == "1") {
              target.style.cursor = "none";
              target.addEventListener("mousemove", (e) => {
                const hoverRect = target.getBoundingClientRect();
                if (
                  e.clientX >= hoverRect.left &&
                  e.clientX <= hoverRect.right &&
                  e.clientY >= hoverRect.top &&
                  e.clientY <= hoverRect.bottom
                ) {
                  designerCursor.classList.add("active");
                  designerCursor.style.left = `${e.clientX}px`;
                  designerCursor.style.top = `${e.clientY}px`;
                }
              });
              target.addEventListener("mouseleave", () => {
                designerCursor.classList.remove("active");
              });
            } else {
              target.classList.add("next-designer-cursor-relative-parent");
              target.appendChild(designerCursor);
            }
          }
        }
      }
    });
}
document.addEventListener("DOMContentLoaded", function (t) {
  bricksIsFrontend && next_designer_cursor();
});
