function next_dynamic_copyright() {
  document
    .querySelectorAll(".next-dynamic-copyright")
    .forEach((dynamicCopyright) => {
      dynamicCopyright.removeAttribute("data-flickering");
      dynamicCopyright.querySelector(
        ".next-dynamic-copyright__year"
      ).textContent = new Date().getFullYear();
    });
}
document.addEventListener("DOMContentLoaded", function (t) {
  document.fonts.ready.then(function () {
    bricksIsFrontend && next_dynamic_copyright();
  });
});
