function next_progress_bar() {
  document.querySelectorAll(".next-progress-bar").forEach((progressBar) => {
    window.addEventListener("scroll", function (e) {
      var bodyHeight = document.body.scrollHeight;
      var scrollTop = window.scrollY;
      var windowHeight = window.innerHeight;
      var percentage = (scrollTop / (bodyHeight - windowHeight)) * 100;
      var inner = progressBar.querySelector(".next-progress-bar__inner");
      if (inner) {
        inner.style.height = percentage + "%";
      }
      var progressNumber = progressBar.querySelector(
        ".next-progress-bar__number"
      );
      if (progressNumber) {
        progressNumber.textContent = Math.round(percentage) + "%";
        progressNumber.style.top = percentage + "%";
      }
    });
  });
}
document.addEventListener("DOMContentLoaded", function (t) {
  bricksIsFrontend && next_progress_bar();
});
