function next_qr_code() {
  document.querySelectorAll(".next-qr-code").forEach((qrCode) => {
    qrCode.removeAttribute("data-flickering");
  });
}
document.addEventListener("DOMContentLoaded", function (t) {
  if (typeof bricksIsFrontend !== "undefined" && bricksIsFrontend) {
    next_qr_code();
  }
});
