function next_rainbow_button() {
  document.querySelectorAll(".next-rainbow-button").forEach((t) => {
    var rainbowColors = t
      .getAttribute("data-gradient-colors")
      .split("next11")
      .filter(Boolean);
    if (rainbowColors.length > 0) {
      t.style.setProperty("--rainbow-colors", rainbowColors.join(", "));
    }
  });
}
document.addEventListener("DOMContentLoaded", function (e) {
  bricksIsFrontend && next_rainbow_button();
});
