function next_shiny_button() {
  document.querySelectorAll(".nb-shiny-button").forEach((button) => {
    button.removeAttribute("data-flickering");
    const effectsContainer = button.querySelector(".nb-shiny-button__effects");
    button.addEventListener("pointermove", (e) => {
      const rect = button.getBoundingClientRect();
      const mouseX = (e.clientX - rect.left) / rect.width;
      const mouseY = (e.clientY - rect.top) / rect.height;
      effectsContainer.style.setProperty("--mx", `${mouseX * 100}%`);
      effectsContainer.style.setProperty("--my", `${mouseY * 100}%`);
    });
  });
}
document.addEventListener("DOMContentLoaded", function (t) {
  document.fonts.ready.then(function () {
    bricksIsFrontend && next_shiny_button();
  });
});
