function next_toolteam() {
  document.querySelectorAll(".next-toolteam__avatar").forEach((img) => {
    const tooltip = img.parentElement.querySelector(".next-toolteam__tooltip");
    img.addEventListener("mousemove", (e) => {
      const rect = e.target.getBoundingClientRect();
      const mouseX = e.clientX - rect.left - rect.width / 2;
      const rotateValue = (mouseX / rect.width) * 45;
      const translateValue = (mouseX / rect.width) * 50;
      tooltip.style.transform = `translateX(calc(-50% + ${translateValue}px)) rotate(${rotateValue}deg) scale(1)`;
      tooltip.style.opacity = "1";
    });
    img.addEventListener("mouseleave", () => {
      tooltip.style.opacity = "0";
      tooltip.style.transform =
        "translateX(-50%) rotate(0deg) scale(var(--tooltip-scale-from))";
    });
  });
}
document.addEventListener("DOMContentLoaded", function (t) {
  bricksIsFrontend && next_toolteam();
});
